import { createRouter, createWebHistory } from "vue-router";
import Dashboard from "../views/Dashboard.vue";
import Home from "../views/Home.vue";

import Login from "../views/login.vue";
import Register from "../views/Register.vue";
import Products from "../views/Products.vue";
import Orders from "../views/Orders.vue";
import ShoppableImage from "../views/ShoppableImage.vue";
import MessageToBuyer from "../views/MessageToBuyer.vue";


const router = createRouter({
  history: createWebHistory(),
  routes: [
    {
      path: '/',
      component: Login
    },
    {path: '/register', component: Register},
    {
      path: "/home",
      component: Home,
      meta: { title: "Home" },
      
      children: [
        { path: '', redirect: '/dashboard' ,meta: { title: "Dashboard"}},
        { path: '/dashboard', component: Dashboard ,meta: { title: "Dashboard" },
        beforeEnter: (to, from, next) => {
          // Redirect to the Dashboard page if the root path is accessed
            if (localStorage.getItem("is_expanded") !== "true") {
              localStorage.setItem("is_expanded", "true");
              }
             next();}},
        { path: '/products', component: Products ,meta: { title: "Products" } },
        { path: '/orders', component: Orders ,meta: { title: "Orders" }},
        { path: '/shoppable_image', component: ShoppableImage ,meta: { title: "ShoppableImage" }},
        { path: '/message_to_buyer', component: MessageToBuyer ,meta: { title: "Message To Buyer" }}
      ]
      
    },
  
  ],
});

export default router;
