// store/index.js
import { createStore } from 'vuex';

export default createStore({
  state: {
    dotsArr: [], // Initial state for dotsArr
  },
  mutations: {
    SET_DOTS_ARR(state, dotsArr) {
      state.dotsArr = dotsArr; // Mutate the dotsArr state
    },
  },
  actions: {
    updateDotsArr({ commit }, newDotsArr) {
      commit('SET_DOTS_ARR', newDotsArr); // Dispatch action to commit mutation
    },
  },
  modules: {},
});
