import "core-js/modules/es.array.push.js";
import { ref } from 'vue';

// Define props

export default {
  __name: 'Hotspots',
  props: {
    uploadedImage: String,
    // Accepts the URL of the uploaded image from the parent component
    hotspots: Array // Accepts an array of hotspots from the parent component
  },
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const props = __props;

    // Define ref for the image description
    const imageDescription = ref('Uploaded Image');

    // Function to add a hotspot at the clicked position
    const addHotspot = event => {
      const rect = event.target.getBoundingClientRect();
      const x = event.clientX - rect.left;
      const y = event.clientY - rect.top;
      hotspots.value.push({
        x,
        y
      });
    };
    const __returned__ = {
      props,
      imageDescription,
      addHotspot,
      ref
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
