// store/index.js
import { createStore } from 'vuex';

export default createStore({
  state: {
    dotsArr: [] // Initial state for dotsArr
  },
  mutations: {
    // Mutation to update dotsArr state
    updateDotsArr(state, newDotsArr) {
      state.dotsArr = newDotsArr;
    }
  },
  actions: {
    // Action to commit the updateDotsArr mutation
    commitUpdateDotsArr({ commit }, newDotsArr) {
      commit('updateDotsArr', newDotsArr);
    }
  },
  modules: {}
});
